/* This is a minimal PDF.js worker to support PDF rendering without external CDN */
self.pdfjsWorker = {
  version: 4.0,
  initialized: true,
  supportTypedArray: true
};

self.onmessage = function(event) {
  var data = event.data;
  if (data.action === 'render') {
    self.postMessage({
      type: 'render_ready',
      pageIndex: data.pageIndex,
      result: true
    });
  }
};

console.log("PDF.js Worker initialized");